# Breadth-First Search (BFS) implementation for a graph
from collections import deque

def bfs(graph, start, goal):
    queue = deque([[start]])
    visited = {start}
    while queue:
        path = queue.popleft()
        node = path[-1]
        if node == goal:
            return path
        for neighbor in graph.get(node, []):
            if neighbor not in visited:
                visited.add(neighbor)
                new_path = list(path)
                new_path.append(neighbor)
                queue.append(new_path)
    return None

graph = {'A': ['B', 'C'], 'B': ['D'], 'C': ['E'], 'D': [], 'E': []}
print(f"BFS Path from A to E: {bfs(graph, 'A', 'E')}")

// Example 2
# Depth-First Search (DFS) implementation for a graph (recursive)
def dfs(graph, start, goal, path=None, visited=None):
    if path is None: path = [start]
    if visited is None: visited = {start}
    if start == goal: return path

    for neighbor in graph.get(start, []):
        if neighbor not in visited:
            visited.add(neighbor)
            result = dfs(graph, neighbor, goal, path + [neighbor], visited)
            if result: return result
    return None

print(f"DFS Path from A to E: {dfs(graph, 'A', 'E')}")

