# A simple semantic network using a dictionary
semantic_network = {
    'Canary': {'is_a': 'Bird', 'color': 'Yellow'},
    'Penguin': {'is_a': 'Bird', 'can_fly': False},
    'Bird': {'is_a': 'Animal', 'has': 'Wings', 'can_fly': True},
    'Animal': {'moves': True}
}

# Reasoning through inheritance
def get_property(entity, prop):
    if prop in semantic_network.get(entity, {}):
        return semantic_network[entity][prop]
    # If not found, check the parent class
    parent = semantic_network.get(entity, {}).get('is_a')
    if parent:
        return get_property(parent, prop)
    return None

print(f"Does a Canary fly? {get_property('Canary', 'can_fly')}")
print(f"Does a Penguin fly? {get_property('Penguin', 'can_fly')}")
print(f"Does a Canary move? {get_property('Canary', 'moves')}")

