# Answering a simple query on the knowledge graph
def query_kg(subject, predicate, kg):
    results = []
    for s, p, o in kg:
        if s == subject and p == predicate:
            results.append(o)
    return results

collaborators = query_kg('Charles Babbage', 'collaborated_with', knowledge_graph)
print(f"\nWho did Charles Babbage collaborate with? {collaborators}")

