# The fundamental task of an LLM: Next-word prediction
import random

# A very simple, probabilistic model for a short phrase
next_word_probabilities = {
    'the cat sat on the': {'mat': 0.8, 'chair': 0.15, 'floor': 0.05}
}

def predict_next_word(prompt):
    if prompt in next_word_probabilities:
        options = next_word_probabilities[prompt]
        return random.choices(list(options.keys()), weights=list(options.values()), k=1)[0]
    return '...'

prompt = 'the cat sat on the'
prediction = predict_next_word(prompt)

print("LLM'S CORE TASK:")
print(f"Prompt: '{prompt}' -> Prediction: '{prediction}'")

