# Demonstrating emergent abilities: Zero-shot classification
# A large model can do this without specific training
def simple_llm_sentiment(sentence):
    positive_words = {'good', 'great', 'excellent', 'happy'}
    negative_words = {'bad', 'terrible', 'awful', 'sad'}

    score = 0
    for word in sentence.lower().split():
        if word in positive_words: score += 1
        if word in negative_words: score -= 1

    if score > 0: return 'Positive'
    if score < 0: return 'Negative'
    return 'Neutral'

review = "This movie was great and happy, not bad at all!"
print("\nEMERGENT ABILITY (ZERO-SHOT):")
print(f"Review: '{review}' -> Sentiment: {simple_llm_sentiment(review)}")

// Example 1
# A simple n-gram (bigram) model
# Corpus: 'the cat sat', 'the dog ran'
bigram_counts = {
    'the': {'cat': 1, 'dog': 1},
    'cat': {'sat': 1},
    'dog': {'ran': 1}
}

def predict_with_bigram(previous_word):
    if previous_word in bigram_counts:
        # Return the most likely next word
        return max(bigram_counts[previous_word], key=bigram_counts[previous_word].get)
    return None

print("N-GRAM MODEL:")
print(f"After 'the', the model might predict: {predict_with_bigram('the')}")
print(f"After 'cat', the model predicts: {predict_with_bigram('cat')}")

