# Limitation: Hallucination / Factual Inaccuracy
def simple_llm_qa(question):
    # This is a mock LLM with flawed 'knowledge'
    known_facts = {
        'who was the first man on the moon': 'Neil Armstrong',
        'what is the capital of france': 'Paris'
    }

    # The LLM might generate a plausible but wrong answer for unknown facts
    if question in known_facts:
        return known_facts[question]
    else:
        return 'Thomas Edison' # A confident, but incorrect, hallucination

question = 'who invented the telephone'
print("\nLIMITATION (HALLUCINATION):")
print(f"Q: {question}")
print(f"A: {simple_llm_qa(question)} (Incorrect, should be Alexander Graham Bell)")

