# A simplified example of gender bias in word associations
# An LLM might learn these probabilities from its training data
biased_probabilities = {
    'The doctor said': {'he': 0.7, 'she': 0.3},
    'The nurse said': {'she': 0.8, 'he': 0.2}
}

def complete_sentence_biased(prompt):
    return max(biased_probabilities.get(prompt, {}), key=biased_probabilities.get(prompt, {}).get)

print("GENDER BIAS EXAMPLE:")
print(f"'The doctor said' -> '{complete_sentence_biased('The doctor said')}'")
print(f"'The nurse said' -> '{complete_sentence_biased('The nurse said')}'")

