# A simplified representation of a risk-based AI regulation framework
def classify_ai_risk(application):
    high_risk_apps = {'medical_diagnosis', 'loan_approval', 'autonomous_driving'}
    low_risk_apps = {'spam_filter', 'recommendation_engine'}

    if application in high_risk_apps:
        return "High-Risk: Requires strict oversight and auditing."
    elif application in low_risk_apps:
        return "Low-Risk: General transparency rules apply."
    else:
        return "Minimal-Risk"

print("AI REGULATION (RISK-BASED APPROACH):")
print(f"Spam filter risk level: {classify_ai_risk('spam_filter')}")
