int main() {
   printf("C programming is a great start!\n");
   return 0;
}

// Example 1: Code with a preprocessor macro
#include <stdio.h>
#define GREETING "Hello from the preprocessor!"
int main() {
   // The preprocessor replaces GREETING with the string
   printf("%s\n", GREETING);
