}

// Example 1: Declaring and using various data types
#include <stdio.h>
int main() {
   int age = 30;
   float weight = 65.5f; // 'f' suffix for float
   double pi = 3.1415926535;
   char initial = 'J';
   printf("Age: %d, Weight: %.1f, Pi: %lf, Initial: %c\n", age, weight, pi, initial);
   return 0;
}

// Example 2: Using type modifiers
#include <stdio.h>
int main() {
   unsigned int positive_num = 40000;
   long long very_large_num = 9876543210LL;
   short small_num = 100;
   printf("Unsigned: %u\n", positive_num);
   printf("Short: %hd\n", small_num);
   printf("Long Long: %lld\n", very_large_num);
   return 0;
}

// Example 1: Using the 'const' keyword
#include <stdio.h>
int main() {
   const int SPEED_OF_LIGHT = 299792458; // in m/s
   printf("The speed of light is %d m/s.\n", SPEED_OF_LIGHT);
   // SPEED_OF_LIGHT = 300000000; // This would cause a compile error
   return 0;
}

// Example 2: Using the #define preprocessor directive
#include <stdio.h>
#define TAX_RATE 0.15
int main() {
   double price = 100.0;
   double tax = price * TAX_RATE;
   double total = price + tax;
   printf("Total price after tax: $%.2f\n", total);
   return 0;
}

// Example 1: Implicit type conversion
#include <stdio.h>
int main() {
   int num_int = 10;
   double num_double = 5.5;
   // num_int is implicitly converted to double before addition
   double sum = num_int + num_double;
   printf("Implicit conversion sum: %f\n", sum);
   return 0;
}

// Example 2: Explicit type conversion (casting)
#include <stdio.h>
int main() {
   int total_marks = 430;
   int num_subjects = 5;
   // Without casting, result would be 86 (integer division)
   double average = (double)total_marks / num_subjects;
   printf("Average marks: %.2f\n", average);
   return 0;
}

// Example 1: Using 'static' to retain value between calls
#include <stdio.h>
void counter_func() {
   static int count = 0; // Initialized only once
   count++;
   printf("This function has been called %d time(s).\n", count);
}
int main() {
   counter_func();
   counter_func();
   counter_func();
   return 0;
}

// Example 2: Using 'auto' (implicitly) and 'register'
#include <stdio.h>
int main() {
   auto int a = 10; // 'auto' is the default for local variables
   register int i;
   printf("The value of 'a' is %d.\n", a);
   // 'i' is a suggestion for the compiler to store in a register
   for (i = 0; i < 5; i++) {
       printf("Loop count: %d\n", i);
   }
   return 0;
}

// Example 1: Basic arithmetic operations
#include <stdio.h>
int main() {
   int a = 15, b = 4;
   printf("Addition: %d\n", a + b);
   printf("Subtraction: %d\n", a - b);
   printf("Multiplication: %d\n", a * b);
   printf("Integer Division: %d\n", a / b); // Result is 3
   printf("Modulus: %d\n", a % b); // Remainder is 3
   return 0;
}

// Example 2: Floating-point division
#include <stdio.h>
int main() {
   double x = 15.0, y = 4.0;
   // Using doubles ensures floating-point division
   printf("Floating-Point Division: %f\n", x / y);
   return 0;
}

// Example 1: Comparing two integers
#include <stdio.h>
int main() {
   int a = 10, b = 20;
   printf("Is a == b? %d\n", a == b); // 0 (false)
   printf("Is a != b? %d\n", a != b); // 1 (true)
   printf("Is a < b? %d\n", a < b);   // 1 (true)
   printf("Is a >= b? %d\n", a >= b); // 0 (false)
   return 0;
}

// Example 2: Using relational operators in an if statement
#include <stdio.h>
int main() {
   int age = 18;
   if (age >= 18) {
       printf("You are eligible to vote.\n");
   } else {
       printf("You are not eligible to vote.\n");
   }
   return 0;
}

// Example 1: Using logical AND (&&)
#include <stdio.h>
int main() {
   int age = 25;
   int has_license = 1; // 1 for true
   if (age >= 18 && has_license == 1) {
       printf("You are allowed to drive.\n");
   }
   return 0;
}

// Example 2: Using logical OR (||) and NOT (!)
#include <stdio.h>
int main() {
   int is_weekend = 0; // 0 for false
   int is_holiday = 1; // 1 for true
   if (is_weekend || is_holiday) {
       printf("Time to relax!\n");
   }
   if (!is_weekend) {
       printf("It's a weekday.\n");
   }
   return 0;
}

// Example 1: Bitwise AND, OR, XOR
#include <stdio.h>
int main() {
   int a = 5;  // 0101 in binary
   int b = 3;  // 0011 in binary
   printf("a & b = %d\n", a & b); // 0001 -> 1
   printf("a | b = %d\n", a | b); // 0111 -> 7
   printf("a ^ b = %d\n", a ^ b); // 0110 -> 6
   return 0;
}

// Example 2: Bitwise Shift operators
#include <stdio.h>
int main() {
   int num = 8; // 1000 in binary
   // Left shift by 1 (8 * 2 = 16)
   printf("num << 1 = %d\n", num << 1);
   // Right shift by 2 (8 / 4 = 2)
   printf("num >> 2 = %d\n", num >> 2);
   return 0;
}

// Example 1: Post-increment (i++)
#include <stdio.h>
int main() {
   int a = 5;
   int b = a++; // b gets original value of a, then a is incremented
   printf("a = %d, b = %d\n", a, b); // a will be 6, b will be 5
   return 0;
}

// Example 2: Pre-increment (++i)
#include <stdio.h>
int main() {
   int x = 5;
   int y = ++x; // x is incremented, then y gets the new value
   printf("x = %d, y = %d\n", x, y); // x will be 6, y will be 6
   return 0;
}

// Example 1: Demonstrating precedence (multiplication before addition)
#include <stdio.h>
int main() {
   int result = 5 + 3 * 2; // 3 * 2 is evaluated first
   printf("Result: %d\n", result); // Prints 11
   return 0;
}

// Example 2: Using parentheses to override precedence
#include <stdio.h>
int main() {
   // (5 + 3) is evaluated first due to parentheses
   int result = (5 + 3) * 2;
   printf("Result with parentheses: %d\n", result); // Prints 16
   return 0;
}

// Example 1: Simple if-else statement
#include <stdio.h>
int main() {
   int number = -10;
   if (number >= 0) {
       printf("The number is positive or zero.\n");
   } else {
       printf("The number is negative.\n");
   }
   return 0;
}

// Example 2: Using if-else if-else ladder
#include <stdio.h>
int main() {
   int score = 85;
   if (score >= 90) {
       printf("Grade: A\n");
   } else if (score >= 80) {
       printf("Grade: B\n");
   } else if (score >= 70) {
       printf("Grade: C\n");
   } else {
       printf("Grade: F\n");
   }
   return 0;
}

// Example 1: A simple switch statement for days of the week
#include <stdio.h>
int main() {
   int day = 3;
   switch (day) {
       case 1: printf("Monday\n"); break;
       case 2: printf("Tuesday\n"); break;
       case 3: printf("Wednesday\n"); break;
       case 4: printf("Thursday\n"); break;
       case 5: printf("Friday\n"); break;
       default: printf("Weekend\n"); break;
   }
   return 0;
}

// Example 2: Switch with fall-through behavior
#include <stdio.h>
int main() {
   char grade = 'B';
   switch (grade) {
       case 'A':
       case 'B':
       case 'C':
           printf("Passed\n");
           break;
       case 'F':
           printf("Failed\n");
           break;
       default: printf("Invalid Grade\n");
   }
   return 0;
}

// Example 1: A simple for loop to print numbers 1 to 5
#include <stdio.h>
int main() {
   for (int i = 1; i <= 5; i++) {
       printf("%d \n", i);
   }
   return 0;
}

// Example 2: A for loop to calculate the sum of first 10 natural numbers
#include <stdio.h>
int main() {
   int sum = 0;
   for (int i = 1; i <= 10; i++) {
       sum += i;
   }
   printf("The sum is: %d\n", sum);
   return 0;
}

// Example 1: A while loop for user input validation
#include <stdio.h>
int main() {
   int number = 0;
   while (number <= 0) {
       printf("Enter a positive number: ");
       scanf("%d", &number);
   }
   printf("You entered: %d\n", number);
   return 0;
}

// Example 2: A do-while loop for a simple menu
#include <stdio.h>
int main() {
   int choice;
   do {
       printf("Menu:\n1. Start\n2. Exit\n");
       printf("Enter your choice: ");
       scanf("%d", &choice);
   } while (choice != 2);
   printf("Exiting...\n");
   return 0;
}

// Example 1: Using 'break' to find a number in an array
#include <stdio.h>
int main() {
   int numbers[] = {10, 20, 30, 40, 50};
   int target = 30;
   for (int i = 0; i < 5; i++) {
       if (numbers[i] == target) {
           printf("Found %d at index %d.\n", target, i);
           break; // Exit the loop once found
       }
   }
   return 0;
}

// Example 2: Using 'continue' to print only odd numbers
#include <stdio.h>
int main() {
   for (int i = 1; i <= 10; i++) {
       if (i % 2 == 0) {
           continue; // Skip the rest of the loop for even numbers
       }
       printf("%d ", i);
   }
   printf("\n");
   return 0;
}

// Example 1: A rare but valid use-case for goto (cleanup)
#include <stdio.h>
#include <stdlib.h>
int main() {
   int *ptr = (int*) malloc(sizeof(int));
   if (ptr == NULL) {
      goto cleanup;
   }
   // ... some processing ...
   printf("Processing complete.\n");

cleanup:
   free(ptr);
   printf("Memory freed. Exiting.\n");
   return 0;
}

// Example 2: Simulating a loop with goto (discouraged)
#include <stdio.h>
int main() {
   int i = 1;
loop_start:
   if (i <= 5) {
       printf("%d ", i);
       i++;
       goto loop_start;
   }
   printf("\n");
   return 0;
}

// Example 1: Declaration and Definition are separate
#include <stdio.h>

// Function declaration (prototype)
