   return x * y;
}

// Example 2: Declaration and Definition combined
#include <stdio.h>

// When definition comes before main, a separate prototype is not needed.
int subtract(int x, int y) {
   return x - y;
}

int main() {
   int result = subtract(10, 3);
   printf("Result is: %d\n", result);
   return 0;
}

// Example 1: A function with parameters and a return value
#include <stdio.h>

// This function takes two integers and returns their sum
int add(int num1, int num2) {
   return num1 + num2;
}

int main() {
   int sum = add(15, 7);
   printf("The sum is %d\n", sum);
   return 0;
}

// Example 2: A function with a void return type
#include <stdio.h>

// This function takes a name and prints a greeting. It returns nothing.
void greet(const char* name) {
   printf("Hello, %s!\n", name);
   // No return value needed
}

