// Example 1: Calculating factorial using recursion
#include <stdio.h>

unsigned long long factorial(int n) {
   // Base case: factorial of 0 is 1
   if (n == 0) {
       return 1;
   } else {
       // Recursive step
       return n * factorial(n - 1);
   }
}

int main() {
   int num = 5;
   printf("Factorial of %d is %llu\n", num, factorial(num));
   return 0;
}

// Example 2: Fibonacci sequence using recursion
#include <stdio.h>

int fibonacci(int n) {
   // Base cases
   if (n <= 1) {
       return n;
   }
   // Recursive step
   return fibonacci(n - 1) + fibonacci(n - 2);
}

int main() {
   int num = 7;
   printf("The %dth Fibonacci number is %d\n", num, fibonacci(num));
   return 0;
}

// Example 1: Demonstrating local and global scope
#include <stdio.h>

int global_var = 100; // This is a global variable

void some_function() {
   int local_var = 10; // This is a local variable
   printf("Inside function: local_var = %d\n", local_var);
   printf("Inside function: global_var = %d\n", global_var);
}

int main() {
   some_function();
   // printf("local_var = %d\n", local_var); // This would cause a compile error
   printf("Outside function: global_var = %d\n", global_var);
   return 0;
}

// Example 2: Block scope within a function
#include <stdio.h>
int main() {
   int x = 10; // Scope is the main function
   if (x == 10) {
      int y = 20; // Scope is only inside this if-block
      printf("Inside block: x = %d, y = %d\n", x, y);
   }
   // printf("y = %d\n", y); // Error: y is not accessible here
   return 0;
}

// Example 1: Declaring, initializing, and accessing a 1D array
#include <stdio.h>
int main() {
   int numbers[5] = {10, 20, 30, 40, 50};
   // Access and print the third element (at index 2)
   printf("The third element is: %d\n", numbers[2]);
   // Modify the first element
   numbers[0] = 15;
   printf("The new first element is: %d\n", numbers[0]);
   return 0;
}

// Example 2: Iterating through an array with a for loop
#include <stdio.h>
int main() {
   int grades[4] = {88, 92, 75, 95};
   int sum = 0;
   for (int i = 0; i < 4; i++) {
      printf("Grade %d: %d\n", i, grades[i]);
      sum += grades[i];
   }
   printf("Total sum of grades: %d\n", sum);
   return 0;
}

// Example 1: Declaring, initializing, and accessing a 2D array
#include <stdio.h>
int main() {
   int matrix[2][3] = {{10, 20, 30}, {40, 50, 60}};
   // Access element at row 1, column 2
   printf("Element at [1][2] is: %d\n", matrix[1][2]);
   // Modify element at row 0, column 0
   matrix[0][0] = 15;
   printf("New element at [0][0] is: %d\n", matrix[0][0]);
   return 0;
}

// Example 2: Iterating through a 2D array with nested loops
#include <stdio.h>
int main() {
   int table[3][2] = {{1, 2}, {3, 4}, {5, 6}};
   // Outer loop for rows
   for (int i = 0; i < 3; i++) {
      // Inner loop for columns
      for (int j = 0; j < 2; j++) {
         printf("table[%d][%d] = %d\n", i, j, table[i][j]);
      }
   }
   return 0;
}

// Example 1: Omitting array size during initialization
#include <stdio.h>
int main() {
   // Compiler automatically sets the size to 4
   int values[] = {5, 10, 15, 20};
   printf("The size of the array is %zu elements.\n", sizeof(values)/sizeof(values[0]));
   return 0;
}

// Example 2: Partial initialization (rest are set to 0)
#include <stdio.h>
int main() {
   int data[5] = {100, 200};
   printf("Array elements:\n");
   for (int i = 0; i < 5; i++) {
      printf("%d \n", data[i]); // Prints 100, 200, 0, 0, 0
   }
   return 0;
}

// Example 1: Using strlen() and strcpy()
#include <stdio.h>
#include <string.h>
int main() {
   char src[] = "Hello, C!";
   char dest[20];
   strcpy(dest, src);
   printf("Source: %s\n", src);
   printf("Destination: %s\n", dest);
   printf("Length of source: %zu\n", strlen(src));
   return 0;
}

// Example 2: Using strcat() and strcmp()
#include <stdio.h>
#include <string.h>
int main() {
   char str1[20] = "Apple";
   char str2[] = "Banana";
   strcat(str1, str2); // Appends str2 to str1
   printf("Concatenated string: %s\n", str1);
   if (strcmp("A", "B") < 0) {
      printf("A comes before B alphabetically.\n");
   }
   return 0;
}

// Example 1: String literal automatically adds null terminator
#include <stdio.h>
int main() {
   char message[] = "Hi"; // Array size is 3 ('H', 'i', '\0')
   printf("String: %s\n", message);
   printf("Size in memory: %zu bytes\n", sizeof(message));
   return 0;
}

// Example 2: Manually creating a null-terminated string
#include <stdio.h>
int main() {
   char word[4];
   word[0] = 'C';
   word[1] = 'a';
   word[2] = 't';
   word[3] = '\0'; // Manually adding the null terminator
   printf("The word is: %s\n", word);
   return 0;
}

// Example 1: Declaring, assigning, and dereferencing a pointer
#include <stdio.h>
int main() {
   int var = 25;
   int *p; // Declare a pointer to an integer
   p = &var; // Assign the address of var to pointer p
   printf("Address of var: %p\n", &var);
   printf("Address stored in p: %p\n", p);
   printf("Value of var using *p: %d\n", *p);
   return 0;
}

// Example 2: Modifying a variable through a pointer
#include <stdio.h>
int main() {
   int num = 50;
   int *ptr = &num;
   printf("Original value of num: %d\n", num);
   *ptr = 100; // Modify the value at the address ptr is pointing to
   printf("New value of num: %d\n", num);
   return 0;
}

// Example 1: Incrementing a pointer
#include <stdio.h>
int main() {
   int numbers[] = {10, 20, 30};
   int *p = numbers; // p points to the first element (10)
   printf("First element: %d\n", *p);
   p++; // Move pointer to the next integer element
   printf("Second element: %d\n", *p);
   return 0;
}

// Example 2: Pointer arithmetic to access array elements
#include <stdio.h>
int main() {
   int values[4] = {100, 200, 300, 400};
   int *ptr = values;
   // Access the 3rd element (index 2) using pointer arithmetic
   printf("Element at index 2: %d\n", *(ptr + 2));
   return 0;
}

// Example 1: Basic declaration and usage of a double pointer
#include <stdio.h>
int main() {
   int var = 123;
   int *p1 = &var; // Pointer to var
   int **p2p = &p1; // Pointer to pointer p1
   printf("Value of var = %d\n", var);
   printf("Value using single pointer *p1 = %d\n", *p1);
   printf("Value using double pointer **p2p = %d\n", **p2p);
   return 0;
}

// Example 2: Function modifying a pointer via a double pointer
#include <stdio.h>
#include <stdlib.h>
void allocate_memory(int **ptr) {
   *ptr = (int*)malloc(sizeof(int)); // Modifies the original pointer
}
int main() {
   int *my_ptr = NULL;
   allocate_memory(&my_ptr);
   if (my_ptr != NULL) {
      *my_ptr = 55;
      printf("Value: %d\n", *my_ptr);
      free(my_ptr);
   }
   return 0;
}

// Example 1: Accessing array elements using a pointer
