// Example 2: Traversing a string with a pointer
#include <stdio.h>
void print_string(char *s) {
   while (*s != '\0') {
      printf("%c", *s);
      s++;
   }
   printf("\n");
}
int main() {
   char greeting[] = "Welcome";
   print_string(greeting);
   return 0;
}

// Example 1: Using the dot operator with a structure variable
