// Example 2: Using the arrow operator with a pointer to a structure
#include <stdio.h>

struct Student {
   int id;
   float gpa;
};

int main() {
   struct Student s1;
   struct Student *ptr = &s1;
   ptr->id = 12345;
   ptr->gpa = 3.8f;
