// Example 1: Defining and using a nested structure
#include <stdio.h>

struct Date {
   int day;
   int month;
   int year;
};

struct Person {
   char name[50];
   struct Date birthday;
};

int main() {
   struct Person p1;
   strcpy(p1.name, "John Doe");
   p1.birthday.day = 15;
   p1.birthday.month = 6;
   p1.birthday.year = 1990;
   printf("%s was born on %d-%d-%d.\n", p1.name, p1.birthday.day, p1.birthday.month, p1.birthday.year);
   return 0;
}

// Example 2: Nested structure with pointers
#include <stdio.h>
#include <stdlib.h>

struct Address {
   char city[30];
   int zip_code;
};
struct User {
   char username[20];
   struct Address *addr;
};

int main() {
