// Example 1: Writing a structure to a binary file with fwrite
#include <stdio.h>

typedef struct {
   int id;
   float value;
} Record;

int main() {
   FILE *fp = fopen("data.bin", "wb");
   if (fp == NULL) return 1;
   Record rec = {101, 3.14f};
   fwrite(&rec, sizeof(Record), 1, fp);
   fclose(fp);
   printf("Record written to data.bin\n");
   return 0;
}

// Example 2: Reading a structure from a binary file with fread
#include <stdio.h>

typedef struct {
   int id;
   float value;
} Record;

int main() {
   FILE *fp = fopen("data.bin", "rb");
   if (fp == NULL) return 1;
   Record rec_in;
   fread(&rec_in, sizeof(Record), 1, fp);
   fclose(fp);
   printf("Read from file: ID=%d, Value=%.2f\n", rec_in.id, rec_in.value);
   return 0;
}

