// Example 2: Reading formatted data from a text file with fscanf
#include <stdio.h>
int main() {
   FILE *fp = fopen("log.txt", "r");
   if (fp == NULL) return 1;
   char user[20];
   int access_level;
   // Note: fscanf is simple but can be unsafe with strings.
   fscanf(fp, "User: %s\n", user);
   fscanf(fp, "Access Level: %d\n", &access_level);
   fclose(fp);
   printf("Read from file: User=%s, Level=%d\n", user, access_level);
   return 0;
}

// Example 1: Using 'w' mode (overwrites) vs 'a' mode (appends)
#include <stdio.h>
int main() {
   FILE *fp;
   // 'w' will erase the file if it exists
   fp = fopen("test.txt", "w");
   fprintf(fp, "This is the first line.\n");
   fclose(fp);
   // 'a' will add to the end of the file
   fp = fopen("test.txt", "a");
   fprintf(fp, "This is the second line.\n");
   fclose(fp);
   printf("Check test.txt for output.\n");
   return 0;
}

// Example 2: Using 'r+' to read and then write to a file
#include <stdio.h>
int main() {
   FILE *fp = fopen("data.txt", "w+");
   fputs("12345", fp);
   // Go back to the beginning of the file
   rewind(fp);
   char c = fgetc(fp);
   printf("First char: %c\n", c);
   // Go to the second position and overwrite
   fseek(fp, 1, SEEK_SET);
   fputc('X', fp);
   fclose(fp);
   printf("Check data.txt, it should be '1X345'\n");
   return 0;
}

