// Example 2: Using feof() and ferror() after a read loop
#include <stdio.h>
int main() {
   FILE *fp = fopen("data.txt", "r");
   if (fp == NULL) {
      perror("Error");
      return 1;
   }
   while (fgetc(fp) != EOF) {
      // Process characters...
   }
   if (ferror(fp)) {
      printf("An I/O error occurred while reading.\n");
   } else if (feof(fp)) {
      printf("End of file reached successfully.\n");
   }
   fclose(fp);
   return 0;
}

