// Example 2: A macro with a potential side-effect pitfall
#include <stdio.h>

#define SQUARE(x) ((x) * (x))

int main() {
   int i = 3;
   // This expands to ((i++) * (i++)), which is undefined behavior
   // as 'i' is modified twice without a sequence point.
   // The output is not predictable.
   int result = SQUARE(i++);
   printf("Result is unpredictable. Final i is %d.\n", i);
   return 0;
}

// Example 1: Using malloc and free for a dynamic array
#include <stdio.h>
#include <stdlib.h>

int main() {
   int n = 5;
   int *arr;
   // Allocate memory for 5 integers
   arr = (int*)malloc(n * sizeof(int));
   if (arr == NULL) return 1; // Check for allocation failure
   for (int i = 0; i < n; i++) {
      arr[i] = i + 1;
      printf("%d ", arr[i]);
   }
   printf("\n");
   free(arr); // Release the memory
   return 0;
}

// Example 2: Using calloc to get zero-initialized memory
#include <stdio.h>
#include <stdlib.h>

int main() {
   int *p;
   // Allocate for 3 integers, initialized to zero
   p = (int*)calloc(3, sizeof(int));
   if (p == NULL) return 1;
   for (int i = 0; i < 3; i++) {
      printf("%d ", p[i]); // Prints 0 0 0
   }
   printf("\n");
   free(p);
   return 0;
}
