// Example 1: Mapping TCP/IP to OSI
String mapToOsi(String tcpIpLayer) {
    switch (tcpIpLayer) {
        case "Application":
            return "Session, Presentation, Application (Layers 5-7)";
        case "Transport":
            return "Transport (Layer 4)";
        case "Internet":
            return "Network (Layer 3)";
        case "Link":
            return "Physical, Data Link (Layers 1-2)";
        default:
            return "Unknown Layer";
    }
}

System.out.println("TCP/IP Internet Layer maps to OSI: " + mapToOsi("Internet"));

