// Example 1: Simulating Manchester Encoding
void manchesterEncode(String bits) {
    System.out.println("Data: " + bits);
    String encoded = "";
    for (char bit : bits.toCharArray()) {
        if (bit == '0') {
            encoded += "L-H "; // Low-to-High transition for 0
        } else {
            encoded += "H-L "; // High-to-Low transition for 1
        }
    }
    System.out.println("Encoded: " + encoded);
}

