// Example 1: Simple Even Parity Check
boolean hasEvenParity(String bits) {
    int oneCount = 0;
    for (char bit : bits.toCharArray()) {
        if (bit == '1') {
            oneCount++;
        }
    }
    return oneCount % 2 == 0;
}

