// Example 2: Conceptual CRC Check
class CrcChecker {
    String calculateCrc(String data) {
        // This is a highly simplified mock-up
        int hash = data.hashCode();
        return Integer.toHexString(hash).substring(0, 4);
    }

    boolean verify(String data, String receivedCrc) {
        return calculateCrc(data).equals(receivedCrc);
    }
}

