// Example 1: Basic MAC address format validation
boolean isValidMacAddress(String mac) {
    // A simple regex to check the format XX:XX:XX:XX:XX:XX
    String regex = "^([0-9A-Fa-f]{2}[:-]){5}([0-9A-Fa-f]{2})$";
    return mac.matches(regex);
}

