// Example 2: Checking TCP flags
void checkFlags(boolean isSyn, boolean isAck) {
    if (isSyn && !isAck) {
        System.out.println("This is the initial SYN packet.");
    } else if (isSyn && isAck) {
        System.out.println("This is the SYN/ACK packet.");
    } else if (!isSyn && isAck) {
        System.out.println("This is the final ACK packet of the handshake.");
    }
}

