// Example 1: Simulating Slow Start
int congestionWindow = 1;
int threshold = 16;
for (int round = 1; round < 5; round++) {
    System.out.println("Round " + round + ": Congestion Window = " + congestionWindow);
    if (congestionWindow < threshold) {
        congestionWindow *= 2; // Exponential growth
    }
}

// Example 2: Simulating AIMD (Additive Increase, Multiplicative Decrease)
int cwnd = 16;
System.out.println("Current cwnd: " + cwnd + " (in Congestion Avoidance)");
// Additive Increase
cwnd += 1;
System.out.println("ACK received, new cwnd: " + cwnd);
// Multiplicative Decrease on packet loss
boolean packetLoss = true;
if (packetLoss) {
    cwnd /= 2;
    System.out.println("Packet loss detected! New cwnd: " + cwnd);
}

