// Example 1: Comparing Wi-Fi standards
void getWifiInfo(String standard) {
    if (standard.equals("802.11g")) {
        System.out.println("Freq: 2.4 GHz, Max Speed: 54 Mbps");
    } else if (standard.equals("802.11ac")) {
        System.out.println("Freq: 5 GHz, Max Speed: >1 Gbps, Feature: MU-MIMO");
    } else if (standard.equals("802.11ax")) {
        System.out.println("Freq: 2.4/5 GHz, Feature: OFDMA, improved efficiency");
    }
}

getWifiInfo("802.11ax");

// Example 2: CSMA/CA Logic for Wi-Fi
void transmitWithCsmaCa() {
    System.out.println("1. Listen to see if the channel is idle.");
    System.out.println("2. If idle, wait a short random time (to avoid collisions).");
    System.out.println("3. Send a small Request-to-Send (RTS) packet.");
    System.out.println("4. Wait for a Clear-to-Send (CTS) response.");
    System.out.println("5. If CTS is received, transmit the data frame.");
}

// Example 1: Generational capabilities
void getGenerationFeatures(String generation) {
    switch (generation) {
        case "2G": System.out.println("Digital Voice + SMS"); break;
        case "3G": System.out.println("Mobile Broadband"); break;
        case "4G": System.out.println("High-speed IP-based Data (LTE)"); break;
        case "5G": System.out.println("High Speed, Low Latency, Massive IoT"); break;
        default: System.out.println("Unknown");
    }
}

getGenerationFeatures("5G");

