// Example 1: Simulating a handoff decision
void checkSignalStrength(int oldSignal, int newSignal) {
    int handoffThreshold = -90; // in dBm
    System.out.println("Current Signal: " + oldSignal + "dBm, Neighbor Signal: " + newSignal + "dBm");
    if (oldSignal < handoffThreshold && newSignal > oldSignal) {
        System.out.println("Decision: Initiate Handoff to new cell.");
    } else {
        System.out.println("Decision: Stay on current cell.");
    }
}

checkSignalStrength(-95, -80);

// Example 2: Hard vs Soft Handoff
void describeHandoff(String type) {
    if (type.equals("Hard")) {
        System.out.println("1. Disconnect from Tower A. 2. Connect to Tower B.");
    } else if (type.equals("Soft")) {
        System.out.println("1. Connect to Tower B. 2. Now connected to A and B. 3. Disconnect from Tower A.");
    }
}

describeHandoff("Soft");

// Example 1: Simulating a CDN request
