    if (userLocation.equals("London")) {
        cdnServer = "London PoP";
    } else {
        cdnServer = "New York PoP";
    }
    System.out.println("Request redirected to nearest server: " + cdnServer);
    System.out.println("Content delivered quickly.");
}

requestContent("London");

