// Example 1: Cloud vs Edge Computing
void processSensorData(String method, boolean isRealTime) {
    System.out.println("Processing sensor data...");
    if (method.equals("Cloud")) {
        System.out.println("Sending all raw data to centralized cloud. High latency.");
    } else if (method.equals("Edge")) {
        System.out.println("Processing data locally on an edge server. Low latency.");
        if (isRealTime) {
            System.out.println("Decision made in milliseconds.");
        }
    }
}

processSensorData("Edge", true);

