double protocolOverhead = 0.10; // 10%
double throughput = bandwidth * (1 - protocolOverhead);

System.out.println("With a bandwidth of " + bandwidth + " Mbps, the actual throughput is " + throughput + " Mbps.");

// Example 2: Impact of latency and jitter
void checkRealTimeAppQuality(int latency, int jitter) {
    System.out.println("Latency: " + latency + "ms, Jitter: " + jitter + "ms");
    if (latency > 150 || jitter > 30) {
        System.out.println("VoIP call quality will likely be poor.");
    } else {
        System.out.println("VoIP call quality should be good.");
    }
}

checkRealTimeAppQuality(200, 10);

// Example 1: Interpreting Ping output
void interpretPing(boolean replyReceived, int time) {
    if (replyReceived) {
        System.out.println("Ping successful! Host is reachable.");
        System.out.println("Round trip time: " + time + "ms.");
    } else {
        System.out.println("Ping failed. Request timed out. Host may be down or unreachable.");
    }
}

interpretPing(true, 25);

// Example 2: Interpreting Traceroute output
void interpretTraceroute() {
    System.out.println("--- Traceroute to example.com ---");
    System.out.println("1. 192.168.1.1 (Your Router)");
    System.out.println("2. 10.0.0.1 (ISP Router)");
    System.out.println("3. * * * (Request timed out - maybe a firewall)");
    System.out.println("4. 93.184.216.34 (Destination)");
    System.out.println("Trace complete.");
}

interpretTraceroute();
