// Example 2: Relational and Logical Operators
#include <iostream>

int main() {
    int x = 5, y = 8;
    std::cout << std::boolalpha; // Print bools as 'true' or 'false'

    std::cout << "x > y: " << (x > y) << std::endl;
    std::cout << "x <= y: " << (x <= y) << std::endl;
    std::cout << "x == 5: " << (x == 5) << std::endl;

    bool condition1 = (x > 0) && (y > 0);
    bool condition2 = (x > y) || (y > 0);
    std::cout << "(x > 0) AND (y > 0): " << condition1 << std::endl;
    std::cout << "(x > y) OR (y > 0): " << condition2 << std::endl;
    return 0;
}

