// Example 1: Using 'this' to resolve name ambiguity
#include <iostream>

class Box {
public:
    // Constructor parameters have same names as member variables
    Box(int width, int height) {
        // 'this->width' refers to the member variable
        // 'width' refers to the function parameter
        this->width = width;
        this->height = height;
    }

    void display() {
        std::cout << "Width: " << this->width << ", Height: " << this->height << std::endl;
    }

private:
    int width;
    int height;
};

int main() {
    Box b(10, 20);
    b.display();
    return 0;
}

// Example 2: Chaining method calls by returning *this
#include <iostream>

class Counter {
public:
    Counter() : value(0) {}

    Counter& increment() {
        value++;
        return *this; // Return a reference to the current object
    }

    Counter& decrement() {
        value--;
        return *this;
    }

    void print() {
        std::cout << "Current value: " << value << std::endl;
    }

private:
    int value;
};

int main() {
    Counter c;
    c.increment().increment().increment().decrement();
    c.print(); // Prints 2
    return 0;
}

