// Example 1: Demonstrating polymorphism with virtual functions
#include <iostream>

class Animal {
public:
    // virtual function
    virtual void speak() {
        std::cout << "Animal makes a sound." << std::endl;
    }
};

class Dog : public Animal {
public:
    // override the virtual function
    void speak() override {
        std::cout << "Dog barks: Woof!" << std::endl;
    }
};

class Cat : public Animal {
public:
    // override the virtual function
    void speak() override {
        std::cout << "Cat meows: Meow!" << std::endl;
    }
};

int main() {
    Animal* pAnimal1 = new Dog();
    Animal* pAnimal2 = new Cat();

    pAnimal1->speak(); // Calls Dog's speak()
    pAnimal2->speak(); // Calls Cat's speak()

    delete pAnimal1;
    delete pAnimal2;
    return 0;
}

