// Example 1: An abstract base class 'Shape'
#include <iostream>

// Abstract base class
class Shape {
public:
    // Pure virtual function, makes Shape an abstract class
    virtual double getArea() = 0;

    void printMessage() {
        std::cout << "I am a shape." << std::endl;
    }
};

class Rectangle : public Shape {
private:
    double width, height;
public:
    Rectangle(double w, double h) : width(w), height(h) {}
    // Provide implementation for the pure virtual function
    double getArea() override {
        return width * height;
    }
};

int main() {
    // Shape s; // Error: Cannot instantiate abstract class
    Rectangle r(5, 4);
    std::cout << "Area of rectangle: " << r.getArea() << std::endl;
    r.printMessage();
    return 0;
}

// Example 2: Using an abstract class as an interface
#include <iostream>

class ILogger {
public:
    virtual void logMessage(const char* message) = 0;
    virtual ~ILogger() {} // Virtual destructor is important for interfaces
};

class ConsoleLogger : public ILogger {
public:
    void logMessage(const char* message) override {
        std::cout << "CONSOLE: " << message << std::endl;
    }
};

void processData(ILogger& logger) {
    logger.logMessage("Processing started.");
    // ... processing logic ...
    logger.logMessage("Processing finished.");
}

int main() {
    ConsoleLogger myLogger;
    processData(myLogger);
    return 0;
}

// Example 1: A class demonstrating encapsulation
#include <iostream>
#include <string>

class Student {
private:
    std::string name;
    int age;

public:
    // Setter for name
    void setName(std::string n) {
        name = n;
    }
    // Getter for name
    std::string getName() {
        return name;
    }
    // Setter for age with validation
    void setAge(int a) {
        if (a > 0) {
            age = a;
        } else {
            std::cout << "Invalid age!" << std::endl;
        }
    }
    // Getter for age
    int getAge() {
        return age;
    }
};

int main() {
    Student s1;
    s1.setName("Alice");
    s1.setAge(20);
    std::cout << "Name: " << s1.getName() << ", Age: " << s1.getAge() << std::endl;
    s1.setAge(-5); // This will print an error message
    return 0;
}

// Example 2: Encapsulation for a Bank Account
#include <iostream>

class BankAccount {
private:
    double balance;

public:
    BankAccount() : balance(0) {}

    double getBalance() {
        return balance;
    }

    void deposit(double amount) {
        if (amount > 0) {
            balance += amount;
        }
    }

    void withdraw(double amount) {
        if (amount > 0 && amount <= balance) {
            balance -= amount;
        }
    }
};

int main() {
    BankAccount acc;
    acc.deposit(100.0);
    acc.withdraw(30.0);
    std::cout << "Final balance: " << acc.getBalance() << std::endl;
    // acc.balance = 10000; // Error: balance is private
    return 0;
}

// Example 1: Overloading the '+' operator for a Complex number class
#include <iostream>

class Complex {
private:
    double real, imag;
public:
    Complex(double r = 0, double i = 0) : real(r), imag(i) {}

    // Overload the + operator
    Complex operator+(const Complex& other) {
        return Complex(real + other.real, imag + other.imag);
    }

    void display() {
        std::cout << real << " + " << imag << "i" << std::endl;
    }
};

int main() {
    Complex c1(3, 4);
    Complex c2(1, 2);
    Complex c3 = c1 + c2; // Calls the overloaded operator+
    c3.display();
    return 0;
}

// Example 2: Overloading the '<<' operator for easy printing
#include <iostream>
#include <string>

class Point {
private:
    int x, y;
public:
    Point(int x = 0, int y = 0) : x(x), y(y) {}

    // Friend function to overload <<
    friend std::ostream& operator<<(std::ostream& os, const Point& p);
};

// Definition of the overloaded operator
std::ostream& operator<<(std::ostream& os, const Point& p) {
    os << "(" << p.x << ", " << p.y << ")";
    return os;
}

