// Example 1: A generic function template to find the maximum of two values
#include <iostream>

// Function template
template <typename T>
T getMax(T a, T b) {
    return (a > b) ? a : b;
}

int main() {
    int i1 = 10, i2 = 20;
    std::cout << "Max of integers: " << getMax(i1, i2) << std::endl;

    double d1 = 5.5, d2 = 3.3;
    std::cout << "Max of doubles: " << getMax(d1, d2) << std::endl;

    char c1 = 'A', c2 = 'Z';
    std::cout << "Max of chars: " << getMax(c1, c2) << std::endl;
    return 0;
}

// Example 2: A function template to swap two values
#include <iostream>

template <typename T>
void swapValues(T& a, T& b) {
    T temp = a;
    a = b;
    b = temp;
}

int main() {
    int x = 5, y = 10;
    std::cout << "Before swap: x = " << x << ", y = " << y << std::endl;
    swapValues(x, y);
    std::cout << "After swap: x = " << x << ", y = " << y << std::endl;

    std::string s1 = "Hello", s2 = "World";
    std::cout << "Before swap: s1 = " << s1 << ", s2 = " << s2 << std::endl;
    swapValues(s1, s2);
    std::cout << "After swap: s1 = " << s1 << ", s2 = " << s2 << std::endl;
    return 0;
}

