// Example 2: A simple generic Stack class
#include <iostream>
#include <vector>

template <typename T>
class Stack {
private:
    std::vector<T> elements;
public:
    void push(const T& element) {
        elements.push_back(element);
    }
    void pop() {
        if (!elements.empty()) elements.pop_back();
    }
    T top() const {
        return elements.back();
    }
    bool isEmpty() const {
        return elements.empty();
    }
};

int main() {
    Stack<int> intStack;
    intStack.push(10);
    intStack.push(20);
    std::cout << "Top of int stack: " << intStack.top() << std::endl;
    return 0;
}

// Example 1: A simple division function that throws an exception
#include <iostream>
#include <stdexcept> // For std::runtime_error

double divide(double numerator, double denominator) {
    if (denominator == 0) {
        throw std::runtime_error("Error: Division by zero!");
    }
    return numerator / denominator;
}

int main() {
    try {
        double result = divide(10.0, 0.0);
        std::cout << "Result: " << result << std::endl;
    } catch (const std::runtime_error& e) {
        std::cout << "Exception caught: " << e.what() << std::endl;
    }
    std::cout << "Program continues after exception." << std::endl;
    return 0;
}

