// Example 2: Using std::shared_ptr for shared ownership
#include <iostream>
#include <memory>

class Resource {
public:
    Resource() { std::cout << "Resource acquired." << std::endl; }
    ~Resource() { std::cout << "Resource released." << std::endl; }
};

int main() {
    std::shared_ptr<Resource> p1;
    {
        std::shared_ptr<Resource> p2 = std::make_shared<Resource>();
        std::cout << "Use count: " << p2.use_count() << std::endl; // Count is 1
        p1 = p2; // Both pointers now share ownership
        std::cout << "Use count: " << p2.use_count() << std::endl; // Count is 2
    } // p2 goes out of scope, count becomes 1
    std::cout << "Use count: " << p1.use_count() << std::endl; // Count is 1
    return 0; // p1 goes out of scope, count becomes 0, resource released
}

