// Example 2: Demonstrating the diamond problem (ambiguity)
#include <iostream>

class PoweredDevice {
public:
    int power = 10;
};

class ScannerDevice : public PoweredDevice {};
class PrinterDevice : public PoweredDevice {};

// Copier inherits two copies of 'power'
class Copier : public ScannerDevice, public PrinterDevice {};

int main() {
    Copier c;
    // std::cout << c.power << std::endl; // AMBIGUITY ERROR!
    // Compiler doesn't know which 'power' to use: the one from
    // ScannerDevice or the one from PrinterDevice.
    std::cout << "This code would not compile due to ambiguity." << std::endl;
    return 0;
}

// Example 1: Solving the diamond problem with virtual inheritance
#include <iostream>

class PoweredDevice {
public:
    int power = 10;
};

// Inherit PoweredDevice virtually
class ScannerDevice : virtual public PoweredDevice {};
class PrinterDevice : virtual public PoweredDevice {};

// Copier now gets only one copy of PoweredDevice members
class Copier : public ScannerDevice, public PrinterDevice {};

int main() {
    Copier c;
    // No ambiguity now!
    std::cout << "Power level: " << c.power << std::endl;
    c.power = 20;
    std::cout << "New power level: " << c.power << std::endl;
    return 0;
}

// Example 2: A more complete example
#include <iostream>

class Animal {
public:
    Animal(int age) : age(age) {}
    void sleep() { std::cout << "Animal is sleeping." << std::endl; }
protected:
    int age;
};

class Mammal : virtual public Animal {
public:
    Mammal(int age) : Animal(age) {}
    void walk() { std::cout << "Mammal is walking." << std::endl; }
};

class WingedAnimal : virtual public Animal {
public:
    WingedAnimal(int age) : Animal(age) {}
    void fly() { std::cout << "Winged animal is flying." << std::endl; }
};

class Bat : public Mammal, public WingedAnimal {
public:
    // We must call the virtual base class constructor from the most derived class
    Bat(int age) : Animal(age), Mammal(age), WingedAnimal(age) {}
    void printAge() { std::cout << "Bat's age is: " << age << std::endl; }
};

int main() {
    Bat b(5);
    b.sleep(); // No ambiguity
    b.printAge();
    return 0;
}

