// Example 1: A race condition (unsafe)
#include <iostream>
#include <thread>
#include <vector>

int counter = 0;

void unsafe_increment() {
    for (int i = 0; i < 100000; ++i) {
        counter++;
    }
}

int main() {
    std::vector<std::thread> threads;
    for (int i = 0; i < 10; ++i) {
        threads.push_back(std::thread(unsafe_increment));
    }
    for (auto& t : threads) {
        t.join();
    }
    // The result will likely NOT be 1,000,000 due to race conditions.
    std::cout << "Final counter (unsafe): " << counter << std::endl;
    return 0;
}

// Example 2: Using std::mutex to fix the race condition
#include <iostream>
#include <thread>
#include <vector>
#include <mutex>

int safe_counter = 0;
std::mutex mtx; // Mutex to protect the counter

void safe_increment() {
    for (int i = 0; i < 100000; ++i) {
        std::lock_guard<std::mutex> lock(mtx); // Lock mutex
        safe_counter++;
    } // Mutex is automatically unlocked when 'lock' goes out of scope
}

int main() {
    std::vector<std::thread> threads;
    for (int i = 0; i < 10; ++i) {
        threads.push_back(std::thread(safe_increment));
    }
    for (auto& t : threads) {
        t.join();
    }
    // The result will now be exactly 1,000,000.
    std::cout << "Final counter (safe): " << safe_counter << std::endl;
    return 0;
}

