#include <vector>
#include <algorithm>

int main() {
    std::vector<int> nums = {1, 2, 3, 4, 5, 6, 7, 8};
    int count = 0;

    // A lambda that captures 'count' by reference to modify it
    int even_count = std::count_if(nums.begin(), nums.end(), [&count](int n) {
        if (n % 2 == 0) {
            count++; // Modify the captured variable
            return true;
        }
        return false;
    });

    std::cout << "Found " << count << " even numbers." << std::endl;
    std::cout << "(count_if returned: " << even_count << ")" << std::endl;
    return 0;
}

// Example 1: Move semantics with std::vector
#include <iostream>
#include <vector>
#include <string>

std::vector<std::string> create_vector() {
    std::vector<std::string> temp = {"one", "two", "three"};
    return temp;
}

int main() {
    // The vector returned by create_vector() is a temporary (rvalue).
    // The move constructor of 'my_vec' is called, avoiding a deep copy.
    std::vector<std::string> my_vec = create_vector();

    std::cout << "my_vec contains: ";
    for (const auto& s : my_vec) {
        std::cout << s << " ";
    }
    std::cout << std::endl;
    return 0;
}

// Example 2: Explicitly using std::move
#include <iostream>
#include <vector>
#include <utility> // For std::move

int main() {
    std::vector<int> vec1 = {10, 20, 30};
    std::cout << "vec1 size before move: " << vec1.size() << std::endl;

    // Transfer ownership of vec1's resources to vec2
    // After this, vec1 is in a valid but likely empty state
    std::vector<int> vec2 = std::move(vec1);

    std::cout << "vec1 size after move: " << vec1.size() << std::endl;
    std::cout << "vec2 size after move: " << vec2.size() << std::endl;

    std::cout << "vec2 contains: ";
    for(int i : vec2) std::cout << i << " ";
    std::cout << std::endl;
    return 0;
}

// Example 1: Basic Node and LinkedList structure
#include <iostream>

struct Node {
    int data;
    Node* next;
    Node(int val) : data(val), next(nullptr) {}
};

class LinkedList {
public:
    Node* head;
    LinkedList() : head(nullptr) {}

    void insert(int val) {
        Node* newNode = new Node(val);
        newNode->next = head;
        head = newNode;
    }

    void display() {
        Node* current = head;
        while (current != nullptr) {
            std::cout << current->data << " -> ";
            current = current->next;
        }
        std::cout << "null" << std::endl;
    }
};

int main() {
    LinkedList list;
    list.insert(10);
    list.insert(20);
    list.insert(30);
    list.display();
    return 0;
}

// Example 2: Deleting the head node in a linked list
#include <iostream>

// (Using the Node and LinkedList classes from Example 1)
// ...

void deleteList(Node*& head) {
    Node* current = head;
    Node* nextNode = nullptr;
    while(current != nullptr) {
        nextNode = current->next;
        delete current;
        current = nextNode;
    }
    head = nullptr;
}

int main() {
    LinkedList list;
    list.insert(1);
    list.insert(2);
    list.display();
    deleteList(list.head);
    std::cout << "After deleting list: ";
    list.display();
    return 0;
}

// Example 1: Basic Binary Search Tree implementation
#include <iostream>

struct Node {
    int data;
    Node *left, *right;
    Node(int val) : data(val), left(nullptr), right(nullptr) {}
};

Node* insert(Node* root, int data) {
    if (root == nullptr) return new Node(data);
    if (data < root->data) {
        root->left = insert(root->left, data);
    } else {
        root->right = insert(root->right, data);
    }
    return root;
}

void inorderTraversal(Node* root) {
    if (root == nullptr) return;
    inorderTraversal(root->left);
    std::cout << root->data << " ";
    inorderTraversal(root->right);
}

int main() {
    Node* root = nullptr;
    root = insert(root, 50);
    insert(root, 30);
    insert(root, 20);
    insert(root, 40);
    insert(root, 70);
    std::cout << "In-order traversal of the BST: ";
    inorderTraversal(root);
    std::cout << std::endl;
    return 0;
}

// Example 2: Searching for a value in the BST
#include <iostream>

// (Using Node struct and insert function from Example 1)
// ...

bool search(Node* root, int key) {
    if (root == nullptr) return false;
    if (root->data == key) return true;
    if (key < root->data) {
        return search(root->left, key);
    } else {
        return search(root->right, key);
    }
}

int main() {
    Node* root = nullptr;
    root = insert(root, 50);
    insert(root, 30);
    insert(root, 70);
    std::cout << std::boolalpha;
    std::cout << "Is 30 in the tree? " << search(root, 30) << std::endl;
    std::cout << "Is 90 in the tree? " << search(root, 90) << std::endl;
    return 0;
}

// Example 1: Basic bitwise operators
#include <iostream>
#include <bitset>

int main() {
    unsigned char a = 0b01010101; // 85
    unsigned char b = 0b11110000; // 240

    std::cout << "a     = " << std::bitset<8>(a) << std::endl;
    std::cout << "b     = " << std::bitset<8>(b) << std::endl;
    std::cout << "a & b = " << std::bitset<8>(a & b) << std::endl;
    std::cout << "a | b = " << std::bitset<8>(a | b) << std::endl;
    std::cout << "a ^ b = " << std::bitset<8>(a ^ b) << std::endl;
    std::cout << "~a    = " << std::bitset<8>(~a) << std::endl;
    return 0;
}

// Example 2: Using bit shifts to check for odd/even
#include <iostream>

// A number is even if its least significant bit is 0
bool isEven(int n) {
    return (n & 1) == 0;
}

int main() {
    int num1 = 10; // binary ...1010
    int num2 = 7;  // binary ...0111

    std::cout << std::boolalpha;
    std::cout << num1 << " is even? " << isEven(num1) << std::endl;
    std::cout << num2 << " is even? " << isEven(num2) << std::endl;

    // Using shifts for fast multiplication/division by 2
    std::cout << "10 * 2 = " << (10 << 1) << std::endl;
    std::cout << "10 / 2 = " << (10 >> 1) << std::endl;
    return 0;
}

