// Example 2: A simple calculator using command-line arguments
#include <iostream>
#include <string>
#include <stdexcept>

// Run from terminal like: ./calculator 10 + 5
int main(int argc, char* argv[]) {
    if (argc != 4) {
        std::cerr << "Usage: " << argv[0] << " <num1> <operator> <num2>" << std::endl;
        return 1;
    }

    try {
        double num1 = std::stod(argv[1]);
        char op = argv[2][0];
        double num2 = std::stod(argv[3]);

        if (op == '+') {
            std::cout << "Result: " << num1 + num2 << std::endl;
        }
    } catch (const std::invalid_argument& e) {
        std::cerr << "Error: Invalid number provided." << std::endl;
        return 1;
    }
    return 0;
}

