// Example 1: Conceptual code for the Singleton Pattern
#include <iostream>

class Logger {
private:
    Logger() {}
public:
    Logger(const Logger&) = delete;
    Logger& operator=(const Logger&) = delete;
    static Logger& getInstance() {
        static Logger instance;
        return instance;
    }
    void log(const std::string& msg) {
        std::cout << "LOG: " << msg << std::endl;
    }
};

int main() {
    Logger::getInstance().log("Application started.");
    return 0;
}

// Example 2: Conceptual code for the Strategy Pattern
#include <iostream>
#include <memory>

class ISortStrategy {
public:
    virtual void sort() = 0;
    virtual ~ISortStrategy() = default;
};
class BubbleSort : public ISortStrategy {
public:
    void sort() override { std::cout << "Sorting with Bubble Sort." << std::endl; }
};

class Sorter {
private:
    std::unique_ptr<ISortStrategy> strategy;
public:
    void setStrategy(std::unique_ptr<ISortStrategy> s) { strategy = std::move(s); }
    void performSort() { if(strategy) strategy->sort(); }
};

int main() {
    Sorter sorter;
    sorter.setStrategy(std::make_unique<BubbleSort>());
    sorter.performSort();
    return 0;
}

// Example 1: 'Before' and 'after' clean code refactoring

// Before: Unclear and hard to read
// int proc(int d) {
//     int t = 0; for (int i=0; i<d; i++) { t+=i; } return t;
// }

// After: Clear and self-documenting
int calculateSumUpTo(int limit) {
    int sum = 0;
    for (int i = 0; i < limit; ++i) {
        sum += i;
    }
    return sum;
}

