// Example 2: Cache-unfriendly vs. cache-friendly memory access
#include <vector>

const int SIZE = 1000;
int matrix[SIZE][SIZE];

// Cache-unfriendly: Jumps around in memory (column-major traversal)
void process_unfriendly() {
    for (int j = 0; j < SIZE; ++j) {
        for (int i = 0; i < SIZE; ++i) {
            matrix[i][j]++;
        }
    }
}

// Cache-friendly: Accesses memory sequentially (row-major traversal)
void process_friendly() {
    for (int i = 0; i < SIZE; ++i) {
        for (int j = 0; j < SIZE; ++j) {
            matrix[i][j]++;
        }
    }
}
