// Example 1
public class VarargsSum {
  // Method with a vararg parameter
  public static int sum(int... numbers) {
    int total = 0;
    for (int num : numbers) {
      total += num;
    }
    return total;
  }

  public static void main(String[] args) {
    System.out.println("Sum 1: " + sum(1, 2, 3));
    System.out.println("Sum 2: " + sum(10, 20, 30, 40, 50));
    System.out.println("Sum 3: " + sum());
  }
}

