// Example 2
import java.nio.file.Files;
import java.nio.file.Path;
import java.nio.file.Paths;
import java.nio.file.StandardCopyOption;
import java.io.IOException;

public class NioCopy {
  public static void main(String[] args) {
    Path source = Paths.get("original.txt");
    Path destination = Paths.get("copy.txt");
    try {
      // Create a dummy source file first
      Files.write(source, "This is the original file.".getBytes());
      Files.copy(source, destination, StandardCopyOption.REPLACE_EXISTING);
      System.out.println("File copied successfully!");
    } catch (IOException e) {
      e.printStackTrace();
    }
  }
}

// Example 1
import java.io.*;

class User implements Serializable {
  String name;
  transient String password; // This field will not be serialized
}

public class SerializationDemo {
  public static void main(String[] args) throws IOException {
    User user = new User();
    user.name = "Alice";
    user.password = "secret123";
    try (ObjectOutputStream oos = new ObjectOutputStream(new FileOutputStream("user.ser"))) {
      oos.writeObject(user);
      System.out.println("User object serialized.");
    }
  }
}

// Example 2
// import java.io.*; // Assuming imports from Example 1

public class DeserializationDemo {
  public static void main(String[] args) throws IOException, ClassNotFoundException {
    User user = null;
    try (ObjectInputStream ois = new ObjectInputStream(new FileInputStream("user.ser"))) {
      user = (User) ois.readObject();
    }
