// Example 1
import java.util.concurrent.ExecutorService;
import java.util.concurrent.Executors;

public class FixedThreadPoolDemo {
  public static void main(String[] args) {
    // Create a pool of 2 threads
    ExecutorService executor = Executors.newFixedThreadPool(2);

    // Submit tasks for execution
    for (int i = 0; i < 5; i++) {
      int taskNum = i;
      executor.submit(() -> {
        System.out.println("Executing task " + taskNum + " by " + Thread.currentThread().getName());
      });
    }

    executor.shutdown(); // Initiates a graceful shutdown
  }
}

// Example 2
import java.util.concurrent.*;

public class CallableExample {
  public static void main(String[] args) throws ExecutionException, InterruptedException {
    ExecutorService executor = Executors.newSingleThreadExecutor();

    // Callable can return a value
    Callable<Integer> task = () -> {
      TimeUnit.SECONDS.sleep(2);
      return 123;
    };

    Future<Integer> future = executor.submit(task);
    System.out.println("Task submitted...");

    Integer result = future.get(); // Blocks until the result is available
    System.out.println("Result from task: " + result);

    executor.shutdown();
  }
}

