const addBtn = document.querySelector('#addItemBtn');
let itemCount = 0;

if (list && addBtn) {
  addBtn.addEventListener('click', () => {
    itemCount++;
    const newItem = document.createElement('li'); // Create a new <li>
    newItem.textContent = `New Item ${itemCount}`;
    list.appendChild(newItem); // Add it to the list
  });
}

