// Example 1
// Converting a .then() chain to async/await.
async function processData() {
  console.log('Start processing...');
  const step1Result = await new Promise(res => setTimeout(() => res('Step 1 complete'), 500));
  console.log(step1Result);
  const step2Result = await new Promise(res => setTimeout(() => res('Step 2 complete'), 500));
  console.log(step2Result);
  return 'All done!';
}

