// Example 1
// A constructor function and its prototype.
function Dog(name) {
  this.name = name;
}

// Methods are added to the prototype to be shared by all instances.
Dog.prototype.bark = function() {
  console.log('Woof!');
};

const myDog = new Dog('Rex');
myDog.bark(); // 'Woof!'
console.log(Object.getPrototypeOf(myDog) === Dog.prototype); // true

// Example 2
// Inheritance using Object.create().
const animal = {
  speak() {
    console.log(`${this.name} makes a noise.`);
  }
};

const cat = Object.create(animal);
cat.name = 'Fluffy';
cat.speak(); // 'Fluffy makes a noise.'

// Example 1
// Inheritance using ES6 classes.
class Animal {
  constructor(name) {
    this.name = name;
  }
  speak() {
    console.log(`${this.name} makes a noise.`);
  }
}

class Cat extends Animal {
  speak() {
    console.log(`${this.name} meows.`);
  }
}

const fluffy = new Cat('Fluffy');
fluffy.speak(); // 'Fluffy meows.'

// Example 2
// Using 'super' to call the parent's method.
class Dog extends Animal {
  constructor(name, breed) {
    super(name); // Call the parent constructor
    this.breed = breed;
  }
  speak() {
    super.speak(); // Call the parent's speak method
    console.log(`${this.name} barks!`);
  }
}

const rex = new Dog('Rex', 'Golden Retriever');
rex.speak();

// Example 1
// Using .call() and .apply().
const person = { name: 'Alice' };

function greet(greeting, punctuation) {
  console.log(`${greeting}, my name is ${this.name}${punctuation}`);
}

greet.call(person, 'Hello', '!');   // Using call
greet.apply(person, ['Hi', '.']); // Using apply

// Example 2
// Using .bind() to create a new function with a bound 'this'.
const person2 = { 
  name: 'Bob',
  sayName: function() { console.log(this.name); }
};

const sayBobsName = person2.sayName.bind(person2);
// No matter how sayBobsName is called, 'this' will always be 'person2'.
sayBobsName(); // 'Bob'

